mapper.update:
update user_info
id=#{id}, real_name=#{realName}, nick_name=#{nickName}, images=#{images}, sex=#{sex}, age=#{age}, phone=#{phone}, register_time=#{registerTime}
id=#{id}

mapper.delete:
delete from user_info
where id in
#{idItem}

delete from user_info
where id in
#{item.id}

mapper.select:
select * from user_info


	AND id=#{id}


	AND real_name=#{realName}


	AND nick_name=#{nickName}


	AND images=#{images}


	AND sex=#{sex}


	AND age=#{age}


	AND phone=#{phone}


	AND register_time=#{registerTime}

select * from user_info


	AND id=#{id}


	AND real_name=#{realName}


	AND nick_name=#{nickName}


	AND images=#{images}


	AND sex=#{sex}


	AND age=#{age}


	AND phone=#{phone}


	AND register_time=#{registerTime}


	limit #{startIndex},#{limit}

select count(*) from user_info


	AND id=#{id}


	AND real_name=#{realName}


	AND nick_name=#{nickName}


	AND images=#{images}


	AND sex=#{sex}


	AND age=#{age}


	AND phone=#{phone}


	AND register_time=#{registerTime}
