mapper.update:
update dict_data
id=#{id}, dict_code=#{dictCode}, dict_name=#{dictName}, dict_value=#{dictValue}, dict_soft=#{dictSoft}, create_time=#{createTime}
id=#{id}

mapper.delete:
delete from dict_data
where id in
#{idItem}

delete from dict_data
where id in
#{item.id}

mapper.select:
select * from dict_data


	AND id=#{id}


	AND dict_code=#{dictCode}


	AND dict_name=#{dictName}


	AND dict_value=#{dictValue}


	AND dict_soft=#{dictSoft}


	AND create_time=#{createTime}

select * from dict_data


	AND id=#{id}


	AND dict_code=#{dictCode}


	AND dict_name=#{dictName}


	AND dict_value=#{dictValue}


	AND dict_soft=#{dictSoft}


	AND create_time=#{createTime}


	limit #{startIndex},#{limit}

select count(*) from dict_data


	AND id=#{id}


	AND dict_code=#{dictCode}


	AND dict_name=#{dictName}


	AND dict_value=#{dictValue}


	AND dict_soft=#{dictSoft}


	AND create_time=#{createTime}
