mapper.update:
update master_info
id=#{id}, name=#{name}, images=#{images}, desciption=#{desciption}, create_time=#{createTime}, sort=#{sort}
id=#{id}

mapper.delete:
delete from master_info
where id in
#{idItem}

delete from master_info
where id in
#{item.id}

mapper.select:
select * from master_info


	AND id=#{id}


	AND name=#{name}


	AND images=#{images}


	AND desciption=#{desciption}


	AND create_time=#{createTime}


	AND sort=#{sort}

select * from master_info


	AND id=#{id}


	AND name=#{name}


	AND images=#{images}


	AND desciption=#{desciption}


	AND create_time=#{createTime}


	AND sort=#{sort}


	limit #{startIndex},#{limit}

select count(*) from master_info


	AND id=#{id}


	AND name=#{name}


	AND images=#{images}


	AND desciption=#{desciption}


	AND create_time=#{createTime}


	AND sort=#{sort}
