mapper.update:
update violin_info
id=#{id}, name=#{name}, code=#{code}, price=#{price}, author=#{author}, mian_ban=#{mianBan}, bei_ban=#{beiBan}, ce_ban=#{ceBan}, you_qi=#{youQi}, qin_xian=#{qinXian}, qin_ma=#{qinMa}, qin_gong=#{qinGong}, qin_he=#{qinHe}, zhi_ban=#{zhiBan}, xian_zhou=#{xianZhou}, wei_diao=#{weiDiao}, wei_sheng=#{weiSheng}, description=#{description}, images=#{images}, create_time=#{createTime}, view_times=#{viewTimes}, buy_url=#{buyUrl}, market_time=#{marketTime}, is_hot=#{isHot}
id=#{id}

mapper.delete:
delete from violin_info
where id in
#{idItem}

delete from violin_info
where id in
#{item.id}

